                               userAgent.includes('FBAN') || 
                               userAgent.includes('FBAV');

    setIsInstagram(isInstagramBrowser);

    if (isInstagramBrowser) {
      // Show fallback after 3 seconds if there are loading issues
      const timer = setTimeout(() => {
        setShowFallback(true);
